import React from "react";
import { Routes, Route, useNavigate } from "react-router-dom";
import Editor from "./Editor";
import { v4 as uuidV4 } from "uuid";

const Home = () => {
  const navigate = useNavigate();

  const createNewDoc = () => {
    const id = uuidV4();
    navigate(`/doc/${id}`);
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gradient-to-br from-purple-100 to-blue-100 text-center">
      <div>
        <h1 className="text-3xl md:text-5xl font-bold text-gray-800 mb-6">
          Welcome to Collab Editor ✍️
        </h1>
        <p className="text-lg md:text-xl text-gray-600 mb-4">
          Append <code className="bg-gray-200 px-2 py-1 rounded">/doc/&lt;your-doc-id&gt;</code> to the URL or click below.
        </p>
        <button
          onClick={createNewDoc}
          className="mt-4 px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-xl text-lg transition duration-300"
        >
          ➕ Create New Document
        </button>
      </div>
    </div>
  );
};

const App = () => {
  return (
    <Routes>
      <Route path="/" element={<Home />} />
      <Route path="/doc/:id" element={<Editor />} />
    </Routes>
  );
};

export default App;
